mapper.select:
select a.id as id, a.gmt_create as gmtCreate, a.gmt_update as gmtUpdate, a.content as content, a.score as score, a.user_id as userId, u.nickname as userNickName, u.avatar_url as userAvatarUrl, a.order_id as orderId, a.spu_id as spuId, a.sku_id as skuId, sk.title as skuTitle, sp.title as spuTitle from unimall_appraise a LEFT JOIN unimall_user u ON a.user_id = u.id LEFT JOIN unimall_sku sk ON a.sku_id = sk.id LEFT JOIN unimall_spu sp ON a.spu_id = sp.id where 1 = 1
AND a.id = #{id}
AND u.nickname LIKE concat("%",#{userName},"%")
AND sp.title LIKE concat("%",#{spuName},"%")
AND a.order_id = #{orderId}
AND a.score = #{score}
AND a.content LIKE concat("%",#{content},"%")
order by a.id desc limit #{offset},#{limit}

select count(1) from unimall_appraise a LEFT JOIN unimall_user u ON a.user_id = u.id LEFT JOIN unimall_sku sk ON a.sku_id = sk.id LEFT JOIN unimall_spu sp ON a.spu_id = sp.id where 1 = 1
AND a.id = #{id}
AND u.nickname LIKE concat("%",#{userName},"%")
AND sp.title LIKE concat("%",#{spuName},"%")
AND a.order_id = #{orderId}
AND a.score = #{score}
AND a.content LIKE concat("%",#{content},"%")
