mapper.select:
SELECT id, phone, password, login_type as loginType, open_id as openId, nickname, avatar_url AS avatarUrl, `level`, birthday, gender, gmt_last_login AS gmtLastLogin, last_login_ip AS lastLoginIp, `status`, gmt_update AS gmtUpdate, gmt_create AS gmtCreate FROM unimall_user WHERE 1 = 1
AND id = #{id}
AND nickname like concat(concat("%",#{nickname}),"%")
AND `level` = #{level}
AND gender = #{gender}
AND status = #{status}
limit #{offset},#{limit}

